import React, { CSSProperties, PureComponent } from "react";
import _ from "lodash";
// import { is } from 'immutable';
import BetterScroll, { Options } from "@better-scroll/core";
import PullUp from "@better-scroll/pull-up";
import PullDown from "@better-scroll/pull-down";
import ScrollBar from "@better-scroll/scroll-bar";
import Bubble from "./Bubble";
import {
  IReactBScrollProps,
  IReactBScrollStates,
  pullDownRefreshOption,
} from "./type";
import styles from "./index.module.css";

BetterScroll.use(PullUp);
BetterScroll.use(PullDown);
BetterScroll.use(ScrollBar);

class BetterScrollList extends PureComponent<
  IReactBScrollProps,
  IReactBScrollStates
> {
  static defaultProps: Partial<IReactBScrollProps> = {
    probeType: 1,
    click: true,
    scrollbar: true,
    pullDownRefresh: false,
    pullUpLoad: false,
    startY: 0,
    freeScroll: false,
    eventPassthrough: "",
    options: {},
    hasMore: true,
    showPullUpElement: true,
  };

  private getWrapperRef: React.RefObject<HTMLDivElement>;
  private getBubbleRef: React.RefObject<any>;
  private bScrollInstance: BetterScroll;
  private reboundPullDownTimer: NodeJS.Timeout;
  private afterPullDownTimerRef: NodeJS.Timeout;

  constructor(props: IReactBScrollProps) {
    super(props);
    this.getWrapperRef = React.createRef();
    this.getBubbleRef = React.createRef();
    this.bScrollInstance = undefined; // scroll 实例
    this.state = {
      isPullUpLoad: false,
      isPulling: false,
      isPullingDown: false,
      beforePullDown: true,
      pullDownInitTop: -50,
      reachRefreshRegion: false,
      bubbleY: 0,
    };
  }

  getInstance = () => {
    return this.bScrollInstance;
  };

  componentDidMount() {
    this._initScroll();
  }

  // shouldComponentUpdate(nextProps, nextState) {
  //   const thisProps = this.props || {};
  //   const thisState = this.state || {};

  //   if (
  //     Object.keys(thisProps).length !== Object.keys(nextProps).length ||
  //     Object.keys(thisState).length !== Object.keys(nextState).length
  //   ) {
  //     return true;
  //   }

  //   for (const key in nextProps) {
  //     if (!is(thisProps[key], nextProps[key])) {
  //       console.log('props:' + key);
  //       console.log(thisProps[key], nextProps[key]);
  //       console.log('-----------------------');
  //       return true;
  //     }
  //   }

  //   for (const key in nextState) {
  //     if (
  //       thisState[key] !== nextState[key] ||
  //       !is(thisState[key], nextState[key])
  //     ) {
  //       console.log('state:' + key);
  //       console.log(thisState[key], nextState[key]);
  //       console.log('+++++++++++++++++++');
  //       return true;
  //     }
  //   }

  //   return true;
  // }

  componentDidUpdate(prevProps) {
    //console.log('componentDidUpdate');
    if (prevProps.hasMore !== this.props.hasMore) {
      this.refresh();
    }
  }

  _initScroll = () => {
    const {
      probeType,
      click,
      scrollbar,
      pullDownRefresh,
      pullUpLoad,
      startY,
      freeScroll,
      eventPassthrough,
      options,
      onPullingDown,
      onPullingUp,
    } = this.props;

    const wrapperDom = this.getWrapperRef.current;

    if (wrapperDom) {
      const _options = Object.assign(
        {},
        {
          probeType: probeType,
          click: click,
          scrollY: true,
          scrollX: false,
          startY: startY,
          freeScroll: freeScroll,
          eventPassthrough: eventPassthrough,
          pullDownRefresh: pullDownRefresh || Boolean(onPullingDown),
          pullUpLoad: pullUpLoad || Boolean(onPullingUp),
          scrollbar: scrollbar,
        },
        options
      ) as Options;
      this.bScrollInstance = new BetterScroll(wrapperDom, _options);
      this._initEvents();
    }
  };

  _initEvents = () => {
    const {
      scroll,
      beforeScrollStart,
      pullDownRefresh,
      onPullingDown,
      pullUpLoad,
      onPullingUp,
    } = this.props;
    /** 触发时机：正在滚动 */
    if (scroll) {
      this.bScrollInstance.on("scroll", (pos: any) => {
        const { scroll } = this.props;
        scroll(pos);
      });
    }

    /** 触发时机：用户手指放在滚动区域的时候 */
    if (beforeScrollStart) {
      this.bScrollInstance.on("beforeScrollStart", () => {
        const { beforeScrollStart } = this.props;
        beforeScrollStart && beforeScrollStart();
      });
    }

    if (pullDownRefresh || Boolean(onPullingDown)) {
      this._initPullDownRefresh();
    }

    if (pullUpLoad || Boolean(onPullingUp)) {
      this._initPullUpLoad();
    }
  };

  _initPullDownRefresh = () => {
    this.bScrollInstance.on("pullingDown", async () => {
      const { onPullingDown } = this.props;
      this.setState({
        beforePullDown: false,
        isPullingDown: true,
        isPulling: true,
      });
      if (onPullingDown) {
        await onPullingDown();
      }
      this.forceRefresh();
    });

    this.bScrollInstance.on("leaveThreshold", () => {
      this.setState({
        reachRefreshRegion: true,
      });
    });

    this.bScrollInstance.on("touchEnd", () => {
      setTimeout(() => {
        // @ts-ignore
        this.getBubbleRef.current && this.getBubbleRef.current.clearRect();
      }, 100);

      this.bScrollInstance.on("scrollEnd", () => {
        setTimeout(() => {
          // @ts-ignore
          this.getBubbleRef.current && this.getBubbleRef.current.clearRect();
        }, 100);
      });
    });

    this.bScrollInstance.on("scroll", (pos: any) => {
      const { beforePullDown, pullDownInitTop } = this.state;
      if (beforePullDown) {
        this.setState({
          bubbleY: Math.max(0, pos.y + pullDownInitTop),
        });
      } else {
        this.setState({
          bubbleY: 0,
        });
      }
    });
  };

  forceRefresh = () => {
    const { pullDownRefresh, onPullingDown, pullUpLoad, onPullingUp } =
      this.props;
    const { isPullingDown } = this.state;

    if ((pullDownRefresh || Boolean(onPullingDown)) && isPullingDown) {
      this.setState({
        isPulling: false,
        reachRefreshRegion: false,
      });

      this._reboundPullDown().then(() => {
        this._afterPullDown();
      });
    } else if (pullUpLoad || Boolean(onPullingUp)) {
      this.setState({
        isPullUpLoad: false,
      });
      setTimeout(() => {
        this.bScrollInstance.finishPullUp();
        this.refresh();
      }, 500);
    } else {
      this.refresh();
    }
  };

  _reboundPullDown = () => {
    const { pullDownRefresh } = this.props;
    const { stopTime = 600 } = pullDownRefresh as pullDownRefreshOption;
    return new Promise((resolve) => {
      this.reboundPullDownTimer = setTimeout(() => {
        this.setState({
          isPullingDown: false,
        });
        this.bScrollInstance && this.bScrollInstance.finishPullDown();
        resolve("");
      }, stopTime);
    });
  };

  _afterPullDown = () => {
    this.afterPullDownTimerRef = setTimeout(() => {
      this.setState({
        beforePullDown: true,
      });
      this.refresh();
    }, this.bScrollInstance?.options.bounceTime);
  };

  _initPullUpLoad = () => {
    this.bScrollInstance.on("pullingUp", async () => {
      const { hasMore, onPullingUp } = this.props;
      if (hasMore) {
        this.setState({
          isPullUpLoad: true,
        });
        if (onPullingUp) {
          await onPullingUp();
        }
        this.forceRefresh();
      } else {
        this.forceRefresh();
      }
    });
  };

  refresh = () => {
    this.bScrollInstance && this.bScrollInstance.refresh();
  };

  dispose = () => {
    if (this.bScrollInstance) {
      this.bScrollInstance.stop();
      this.bScrollInstance.destroy();
      this.bScrollInstance = null;
    }

    this.reboundPullDownTimer && clearTimeout(this.reboundPullDownTimer);
    this.afterPullDownTimerRef && clearTimeout(this.afterPullDownTimerRef);
  };

  componentWillUnmount() {
    this.dispose();
  }

  getPullUpText = () => {
    const { showPullUpElement = true, hasMore, pullUpLoad } = this.props;
    const moreTxt = _.get(pullUpLoad, "txt.more") || "加载更多";
    const noMoreTxt = _.get(pullUpLoad, "txt.noMore") || "没有更多数据了";
    return showPullUpElement ? (hasMore ? moreTxt : noMoreTxt) : "";
  };

  renderDefaultPullUpElement = () => {
    const { pullUpLoad, onPullingUp } = this.props;
    const { isPullUpLoad } = this.state;
    return (
      <>
        {(pullUpLoad || Boolean(onPullingUp)) && (
          <div className={styles["pullUp-wrapper"]}>
            {isPullUpLoad ? (
              <div className={styles["pullUp-after-trigger"]}>
                <img
                  className={styles["scroll-loading"]}
                  src="data:image/gif;base64,"
                />
              </div>
            ) : (
              <div>
                <span>{this.getPullUpText()}</span>
              </div>
            )}
          </div>
        )}
      </>
    );
  };

  getPullDownText = () => {
    const { pullDownRefresh } = this.props;
    return _.get(pullDownRefresh, "txt") || "刷新成功";
  };

  renderDefaultPullDownElement = () => {
    const { pullDownRefresh, onPullingDown } = this.props;
    const { beforePullDown, bubbleY, isPulling } = this.state;
    return (
      <>
        {(pullDownRefresh || Boolean(onPullingDown)) && (
          <div className={styles["pulldown-wrapper"]}>
            {beforePullDown ? (
              <div className={styles["before-trigger"]}>
                <Bubble bubbleRef={this.getBubbleRef} y={bubbleY} />
              </div>
            ) : (
              <div className={styles["after-trigger"]}>
                {isPulling ? (
                  <div>
                    <img
                      className={styles["scroll-loading"]}
                      src="data:image/gif;base64,"
                    />
                  </div>
                ) : (
                  <div>
                    <span>{this.getPullDownText()}</span>
                  </div>
                )}
              </div>
            )}
          </div>
        )}
      </>
    );
  };

  render() {
    const { PullDownElement, PullUpElement } = this.props;
    const {
      reachRefreshRegion,
      beforePullDown,
      isPulling,
      bubbleY,
      isPullUpLoad,
    } = this.state;
    return (
      <div className={styles.bScrollWrapper} ref={this.getWrapperRef}>
        <div>
          {PullDownElement ? (
            <PullDownElement
              reachRefreshRegion={reachRefreshRegion}
              beforePullDown={beforePullDown}
              isPulling={isPulling}
              pulldownY={bubbleY}
            />
          ) : (
            this.renderDefaultPullDownElement()
          )}
          <div>{this.props.children}</div>

          {PullUpElement ? (
            <PullUpElement isPullUpLoad={isPullUpLoad} />
          ) : (
            this.renderDefaultPullUpElement()
          )}
        </div>
      </div>
    );
  }
}

export default BetterScrollList;
